import { useState } from "react";
import { sculptureList } from "./data";

export default function ArtDisplay() {
  const [index, setIndex] = useState(0);
  const [show, setShow] = useState(false);
  const currentSculpture = sculptureList[index];

  const handleNextClick = () => {
    setIndex((index + 1) % sculptureList.length);
  };
  const toggleShow = () => {
    setShow(!show);
  };

  return (
    <>
      <button onClick={handleNextClick}>next</button>
      <p>
        {currentSculpture.name} by {currentSculpture.artist}
      </p>
      <p>
        (Slide {index + 1} of {sculptureList.length})
      </p>
      <button onClick={toggleShow}>{show ? "Hide" : "Show"} description</button>
      {show && <p>{currentSculpture.description}</p>}
      <img src={currentSculpture.url} />
    </>
  );
}
