import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import ReactSwitch from "react-switch";
import "./index.css";

const root = ReactDOM.createRoot(document.getElementById("root"));
const isLogged = false;
const user = {
  name: "Bob Marley",
  imageSize: "90px",
  imgUrl: "https://i.imgur.com/yXOvdOSs.jpg",
};

function ToggleSwitch() {
  const [checked, setChecked] = useState(true);
  const handleChange = (val) => {
    setChecked(val);
  };
  return (
    <div className="toggle-switch" style={{ textAlign: "center" }}>
      <ReactSwitch checked={checked} onChange={handleChange} />
    </div>
  );
}

function MyButton() {
  return <button>Click me</button>;
}
function FormInput({ inputType, labelTitle, inputName }) {
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <label for={inputName}>{labelTitle}</label>
      <input
        style={{ width: "200px" }}
        type={inputType}
        name={inputName}
        required
      />
    </div>
  );
}
function LoginForm() {
  return (
    <div
      className="form-container"
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "350px",
        borderRadius: "10%",
        background: "gray",
        rowGap: "1em",
        padding: "1.5em",
      }}
    >
      <div
        className="form-header"
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <img
          className="form-icon"
          src={user.imgUrl}
          style={{
            width: user.imageSize,
            height: user.imageSize,
            borderRadius: "50%",
          }}
        />
        <div className="form-title">Sign in to your account</div>
      </div>
      <div className="form">
        <FormInput
          inputType="text"
          inputName="email"
          labelTitle="Email address"
        />
        <FormInput inputType="text" inputName="pwd" labelTitle="Password" />
      </div>
      <button type="button">Sign in</button>
    </div>
  );
}
function LoggedInUser() {
  return (
    <div>
      <h1>{user.name}</h1>
      <img
        className="avatar"
        src={user.imgUrl}
        style={{
          width: user.imageSize,
          height: user.imageSize,
          borderRadius: "50%",
        }}
      />
      <MyButton />
    </div>
  );
}

export default function App() {
  return <>{isLogged ? <LoggedInUser /> : <LoginForm />}</>;
}

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
);
