import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const root = ReactDOM.createRoot(document.getElementById("root"));
const isLogged = false;
const user = {
  name: "Bob Marley",
  imageSize: "90px",
  imgUrl: "https://i.imgur.com/yXOvdOSs.jpg",
};

function ToggleSwitch() {
  // Initialize the state for the switch
  const [isOn, setIsOn] = useState(false);

  // Function to handle the toggle switch click event
  const handleToggle = () => {
    // Toggle the state of the switch
    setIsOn((prevState) => !prevState);
  };
  return (
    <div className="toggle-switch-container">
      <label className="switch">
        <input type="checkbox" checked={isOn} onChange={handleToggle} />
        <span className="slider round"></span>
      </label>
      <span>{isOn ? "ON" : "OFF"}</span>
    </div>
  );
}

function MyButton() {
  return <button>Click me</button>;
}
function FormInput({ inputType, labelTitle, inputName }) {
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <label for={inputName}>{labelTitle}</label>
      <input
        style={{ width: "200px" }}
        type={inputType}
        name={inputName}
        required
      />
    </div>
  );
}
function LoginForm() {
  return (
    <div
      className="form-container"
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "350px",
        borderRadius: "10%",
        background: "gray",
        rowGap: "1em",
        padding: "1.5em",
      }}
    >
      <div
        className="form-header"
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <img
          className="form-icon"
          src={user.imgUrl}
          style={{
            width: user.imageSize,
            height: user.imageSize,
            borderRadius: "50%",
          }}
        />
        <div className="form-title">Sign in to your account</div>
      </div>
      <div className="form">
        <FormInput
          inputType="text"
          inputName="email"
          labelTitle="Email address"
        />
        <FormInput inputType="text" inputName="pwd" labelTitle="Password" />
      </div>
      <button type="button">Sign in</button>
    </div>
  );
}
function LoggedInUser() {
  return (
    <div>
      <h1>{user.name}</h1>
      <img
        className="avatar"
        src={user.imgUrl}
        style={{
          width: user.imageSize,
          height: user.imageSize,
          borderRadius: "50%",
        }}
      />
      <MyButton />
    </div>
  );
}

export default function App() {
  return (
    <>
      <ToggleSwitch />
      {isLogged ? <LoggedInUser /> : <LoginForm />}
    </>
  );
}

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
);
